mapper.update:
UPDATE ST_TEETH_MEASURE
CAVITY_NORMAL = #{cavityNormal}, CAVITY_CAUTION = #{cavityCaution}, CAVITY_DANGER = #{cavityDanger}, PERM_CAVITY_NORMAL = #{permCavityNormal}, PERM_CAVITY_CAUTION = #{permCavityCaution}, PERM_CAVITY_DANGER = #{permCavityDanger},
WHERE 1=1 AND USER_ID = #{userId}
AND DIAG_CD = #{diagCd}
AND DATE_FORMAT(MEASURE_DT, '%Y-%m-%d') = #{measureDt}

mapper.select:

SELECT 
	USER_ID
	,USER_NO
	,${toothNo}
	,DATE_FORMAT(MEASURE_DT, '%Y-%m-%d') AS MEASURE_DT
FROM ST_TEETH_MEASURE
WHERE 1=1 
AND USER_ID = #{userId}
AND DATE_FORMAT(MEASURE_DT, '%Y-%m-%d') BETWEEN #{startDt} AND #{endDt}

ORDER BY MEASURE_DT DESC

mapper.update:
UPDATE ST_TEETH_MEASURE
SET DIAG_CD  =  #{diagCd}
WHERE 1=1
AND USER_ID = #{userId}
AND DATE_FORMAT(MEASURE_DT, '%Y-%m-%d') = #{measureDt}

UPDATE ST_TEETH_MEASURE
SET MEMO  =  #{memo}
WHERE 1=1
AND USER_ID = #{userId}
AND DATE_FORMAT(MEASURE_DT, '%Y-%m-%d') = #{measureDt}

UPDATE ST_TEETH_INFO
SET TEETH_STATUS  =  #{teethStatus}
WHERE 1=1
AND USER_ID = #{userId}
AND DATE_FORMAT(RECORD_DT, '%Y-%m-%d') = #{recordDt}

mapper.select:
SELECT COUNT(*) FROM ST_TEETH_INFO
WHERE 1=1 
AND USER_ID = #{userId}
AND DATE_FORMAT(RECORD_DT, '%Y-%m-%d') = #{recordDt} 
ORDER BY RECORD_DT DESC
LIMIT 1

mapper.update:
UPDATE ST_STUDENT_USER_DETAIL
SET IS_MEASURING  =  #{isMeasuring}
WHERE USER_ID = #{userId}


UPDATE ST_TEETH_MEASURE
SET  DETERIORATE_SCORE  =  #{deteriorateScore}
WHERE 1=1
AND USER_ID = #{userId}
AND DATE_FORMAT(MEASURE_DT, '%Y-%m-%d') = #{measureDt}
