mapper.select:
SELECT SSUD.USER_ID AS USER_ID ,SU.USER_NAME AS USER_NAME ,DATE_FORMAT(SU.USER_BIRTHDAY, '%Y-%m-%d') AS USER_BIRTHDAY ,SU.USER_TEL_NO AS USER_TEL_NO ,SU.USER_SEX AS USER_SEX ,SSUD.SCHOOL_TYPE AS SCHOOL_TYPE ,SSUD.IS_MEASURING AS IS_MEASURING FROM ST_STUDENT_USER_DETAIL SSUD LEFT OUTER JOIN ST_USER SU ON SSUD.USER_ID = SU.USER_ID WHERE 1=1
AND SSUD.TEACHER_ID = #{userId}
GROUP BY SU.USER_ID ORDER BY SU.USER_NAME ASC
