mapper.select:
SELECT SSUD.USER_ID AS userId ,SU.USER_NAME AS userName ,DATE_FORMAT(SU.USER_BIRTHDAY, '%Y-%m-%d') AS userBirthday ,SU.USER_TEL_NO AS userTelNo ,SU.USER_SEX AS userSex ,SSUD.SCHOOL_TYPE AS schoolType ,SSUD.IS_MEASURING AS isMeasuring FROM ST_STUDENT_USER_DETAIL SSUD LEFT OUTER JOIN ST_USER SU ON SSUD.USER_ID = SU.USER_ID WHERE 1=1
AND SSUD.TEACHER_ID = #{userId}
GROUP BY SU.USER_ID ORDER BY SU.USER_NAME ASC
